INSERT INTO `tb_music` VALUES ('4589', ' 望', '张碧晨、赵丽颖 ', '楚乔传', 'http://singerimg.kugou.com/uploadpic/softhead/400/20170106/20170106171217388.jpg', 'http://fs.web.kugou.com/88e44bda6e5fe5693ab027a1acd499d1/593021d6/G095/M07/17/1D/_4YBAFkmjqiAO7QHAD59CTlCc7k803.mp3', '[00:00.67]张碧晨、赵丽颖 - 望(《楚乔传》片头曲)\r\n[00:03.77]作词：刘畅、谭旋\r\n[00:05.08]作曲：谭旋\r\n[00:05.93]编曲：龙隆\r\n[00:14.99]荆棘之上仰望的人\r\n[00:18.81]会看透风尘\r\n[00:22.76]在这善恶彼伏的城\r\n[00:26.48]我与你共存\r\n[00:50.44]那一束光透着锋芒\r\n[00:52.99]指引我穿越悲伤\r\n[00:57.81]那一次的遇见\r\n[01:00.11]命中注定是你\r\n[01:05.11]欲望将心磨去棱角\r\n[01:07.81]嘲笑温情从不胜仗\r\n[01:12.63]我偏要做这野火烧不尽的粮\r\n[01:16.94]把爱喂养\r\n[01:21.69]站在巨浪的中心\r\n[01:23.29]风是我思想\r\n[01:25.09]雨是催马还击的猎场\r\n[01:29.50]抬头遥望那些回不去的时光\r\n[01:32.70]眼前的火炬在激荡\r\n[01:36.10]天越来越黑\r\n[01:37.95]路越来越长\r\n[01:39.46]我知道人心不能摇晃\r\n[01:43.55]敢乱世为王\r\n[01:45.55]必掷血屠狼\r\n[01:47.45]才有希望\r\n[01:57.01]梦里有过渴望\r\n[01:59.11]醒来却容不得泪千行\r\n[02:04.31]曾经暗度陈仓\r\n[02:06.57]如今正面对抗\r\n[02:11.92]不接受命运的盘问\r\n[02:14.37]爱恨教我以柔克刚\r\n[02:19.42]付出血的代价还要一颗心脏\r\n[02:23.73]寸寸偿\r\n[02:28.48]站在巨浪的中心\r\n[02:30.08]风是我思想\r\n[02:31.80]雨是催马还击的猎场\r\n[02:36.15]抬头遥望那些回不去的时光\r\n[02:39.40]眼前的火炬在激荡\r\n[02:42.73]天越来越黑\r\n[02:44.68]路越来越长\r\n[02:46.03]我知道人心不能摇晃\r\n[02:50.23]敢乱世为王\r\n[02:52.14]必掷血屠狼\r\n[02:54.14]才有希望\r\n[03:17.73]荆棘之上仰望的人\r\n[03:21.68]会看透风尘\r\n[03:25.53]在这善恶彼伏的城\r\n[03:29.49]我与你共存\r\n[03:37.15]穿过刀山越过火海\r\n[03:39.00]把爱信仰\r\n[03:40.55]一颗真心有一片天堂\r\n[03:44.65]生如蜉蝣却要活得荡气回肠\r\n[03:47.90]相信前方就是故乡\r\n[03:51.20]我抬头仰望这荆棘之上\r\n[03:54.70]大地的力量终将被释放\r\n[03:58.71]看万物生长看日月沧桑\r\n[04:02.95]为你守望\r\n', '\r\n																											4:15\r\n									', '0', '1');
INSERT INTO `tb_music` VALUES ('4591', ' 默', '那英 ', '何以笙箫默', 'http://singerimg.kugou.com/uploadpic/softhead/400/20160711/20160711151037573639.jpg', 'http://fs.web.kugou.com/dc2bf48021647d36fca416fc7299a3cd/59302299/G018/M04/0A/03/Ug0DAFUzYf2ASiylAE-G25UjDNo036.mp3', '[00:17.22]作词：尹约\r\n[00:28.37]作曲：钱雷\r\n[00:39.31]忍不住化身一条固执的鱼\r\n[00:46.51]逆着洋流独自游到底\r\n[00:53.13]年少时候虔诚发过的誓\r\n[00:59.80]沉默地沉没在深海里\r\n[01:06.42]重温几次\r\n[01:10.07]结局还是\r\n[01:13.08]失去你\r\n[01:20.71]我被爱判处终身孤寂\r\n[01:25.78]不还手不放手\r\n[01:33.94]笔下画不完的圆\r\n[01:37.44]心间填不满的缘是你\r\n[01:48.10]为何爱判处众生孤寂\r\n[01:53.10]挣不脱逃不过\r\n[02:01.50]眉头解不开的结\r\n[02:04.95]命中解不开的劫是你\r\n[02:15.81]啊失去你\r\n[02:29.25]啊失去你\r\n[03:13.92]忍不住化身一条固执的鱼\r\n[03:20.72]逆着洋流独自游到底\r\n[03:27.55]年少时候虔诚发过的誓\r\n[03:34.02]沉默地沉没在深海里\r\n[03:41.05]周而复始\r\n[03:44.35]结局还是\r\n[03:47.40]失去你\r\n[03:54.94]我被爱判处终身孤寂\r\n[04:00.28]不还手不放手\r\n[04:08.49]笔下画不完的圆\r\n[04:11.84]心间填不满的缘是你\r\n[04:22.35]为何爱判处众生孤寂\r\n[04:27.58]挣不脱逃不过\r\n[04:35.79]眉头解不开的结\r\n[04:39.20]命中解不开的劫是你\r\n[04:49.60]啊失去你\r\n[05:03.43]啊我失去你\r\n', '\r\n																											5:25\r\n									', '0', '1');
INSERT INTO `tb_music` VALUES ('4593', ' 桃花诺', 'G.E.M.邓紫棋 ', '上古情歌', 'http://singerimg.kugou.com/uploadpic/softhead/400/20161206/20161206150644553.jpg', 'http://fs.web.kugou.com/4da2faebee52b2cee14ac4c7cba48c02/593029ff/G098/M08/07/19/AocBAFkn4PmASTuWADWju6kYBPk145.mp3', '[00:00.37]邓紫棋 - 桃花诺(电视剧《上古情歌》片尾曲)\r\n[00:01.72]作词：张赢\r\n[00:01.92]作曲：罗锟\r\n[00:02.12]编曲：罗锟 Xueran Chen\r\n[00:02.37]制作人（美国）：Xueran Chen\r\n[00:02.77]制作人（中国）：张赢\r\n[00:03.17]人声录音师：姚海毅\r\n[00:03.57]和音：赵贝尔\r\n[00:03.77]管弦乐团：Hungarian symphonic orchestras\r\n[00:04.12]吉他：Jacob Boyd\r\n[00:04.32]中国笛：Chris Bleth\r\n[00:04.57]大提琴：Ro Rowan\r\n[00:04.77]缩混工程师：NEM Studios\r\n[00:05.12]母带工程师：Randy Merrill\r\n[00:05.42]监制：宋鹏飞\r\n[00:19.68]初见若缱绻\r\n[00:22.53]誓言风吹云舒卷\r\n[00:26.94]岁月间问今夕又何年\r\n[00:33.35]心有犀但愿\r\n[00:36.30]执念轮回过经年\r\n[00:40.75]弹指间繁花开落多少遍\r\n[00:47.10]这一世牵绊纠结\r\n[00:51.81]触动了心弦\r\n[00:54.36]下一世不知可否再见\r\n[01:00.75]留一片桃花纪念\r\n[01:05.30]了却浮生缘\r\n[01:08.15]眉目间还有我的思念\r\n[01:14.96]一寸土一年木\r\n[01:18.22]一花一树一贪图\r\n[01:21.72]情是种爱偏开在迷途\r\n[01:28.57]忘前路忘旧物\r\n[01:31.98]忘心忘你忘最初\r\n[01:35.37]花斑斑留在爱你的路\r\n[01:55.67]这一世牵绊纠结\r\n[02:00.22]触动了心弦\r\n[02:02.82]下一世不知可否再见\r\n[02:09.27]留一片桃花纪念\r\n[02:13.87]了却浮生缘\r\n[02:16.47]眉目间还有我的思念\r\n[02:23.48]一寸土一年木\r\n[02:26.83]一花一树一贪图\r\n[02:30.28]情是种爱偏开在迷途\r\n[02:37.13]忘前路忘旧物\r\n[02:40.59]忘心忘你忘最初\r\n[02:44.18]花斑斑留在爱你的路\r\n[02:50.48]虔诚夙愿来世路\r\n[02:54.23]一念桃花因果渡\r\n[02:57.68]那一念\r\n[02:59.88]几阙时光在重复\r\n[03:04.54]听雨书望天湖\r\n[03:07.99]人间寥寥情难诉\r\n[03:11.29]回忆斑斑\r\n[03:13.49]留在爱你的路\r\n', '\r\n																											3:39\r\n									', '0', '1');
INSERT INTO `tb_music` VALUES ('4595', ' 凉凉', '杨宗纬、张碧晨 ', '三生三世十里桃花', 'http://singerimg.kugou.com/uploadpic/softhead/400/20170109/20170109150710384221.jpg', 'http://fs.web.kugou.com/5d9903fa49ed63caedf97f91a896acf6/59302ccb/G083/M08/0E/04/kw0DAFhvcJSAAX3SAFFchlFbaO8093.mp3', '[00:00.91]杨宗纬、张碧晨 - 凉凉\r\n[00:02.63]作词：刘畅\r\n[00:03.74]作曲：谭旋\r\n[00:04.72]编曲：韦国赟\r\n[00:22.73]女：入夜渐微凉\r\n[00:49.93]繁花落地成霜\r\n[00:52.61]你在远方眺望\r\n[00:55.19]耗尽所有暮光\r\n[00:58.02]不思量自难相忘\r\n[01:06.11]男：夭夭桃花凉\r\n[01:11.33]前世你怎舍下\r\n[01:14.01]这一海心茫茫\r\n[01:16.69]还故作不痛不痒不牵强\r\n[01:24.24]都是假象\r\n[01:27.83]女：凉凉夜色为你思念成河\r\n[01:36.31]化作春泥呵护着我\r\n[01:40.36]男：浅浅岁月拂满爱人袖\r\n[01:45.86]片片芳菲入水流\r\n[01:51.52]女：凉凉天意潋滟一身花色\r\n[01:57.33]落入凡尘伤情着我\r\n[02:01.78]男：生劫易渡情劫难了\r\n[02:05.38]折旧的心还有几分前生的恨\r\n[02:13.37]还有几分\r\n[02:16.14]合：前生的恨\r\n[02:23.27]女：也曾鬓微霜\r\n[02:44.68]也曾因你回光\r\n[02:47.35]悠悠岁月漫长\r\n[02:50.03]怎能浪费时光\r\n[02:52.86]去流浪\r\n[02:53.82]男：去流浪\r\n[02:56.96]女：去换成长\r\n[03:01.21]男：灼灼桃花凉\r\n[03:06.01]今生愈渐滚烫\r\n[03:08.66]一朵已放心上\r\n[03:11.28]足够三生三世背影成双\r\n[03:15.49]女：背影成双\r\n[03:17.56]男：在水一方\r\n[03:22.12]女：凉凉夜色为你思念成河\r\n[03:30.67]化作春泥呵护着我\r\n[03:35.01]男：浅浅岁月拂满爱人袖\r\n[03:40.37]片片芳菲入水流\r\n[03:46.25]女：凉凉天意潋滟一身花色\r\n[03:51.97]落入凡尘伤情着我\r\n[03:56.52]男：生劫易渡情劫难了\r\n[04:00.03]折旧的心还有几分前生的恨\r\n[04:07.98]女：凉凉三生三世恍然如梦\r\n[04:21.26]须臾的年风干泪痕\r\n[04:26.07]男：若是回忆不能再相认\r\n[04:31.08]就让情分落九尘\r\n[04:37.46]女：凉凉十里何时还会春盛\r\n[04:42.66]又见树下一盏风存\r\n[04:47.22]男：落花有意流水无情\r\n[04:50.66]别让恩怨爱恨凉透那花的纯\r\n[04:58.30]合：吾生愿牵尘\r\n', '\r\n																											5:33\r\n									', '0', '1');
